#'     
#' # use Nelder-Mead
#' objfun = function(x) sum(c((x[1] - 3), (x[2] + 1))^2)
#' ret = tao(objfun, c(1,2), "nm", 2)
#' ret$x
tao <- function(par, fn, method, k, n = 1L, control = NULL) {
    .Call('taoR_tao', PACKAGE = 'taoR', par, fn, method, k, n, control)
}

